// Load the original GrapeJS first
(function() {
    const script = document.createElement('script');
    script.src = 'https://unpkg.com/grapesjs@0.20.3/dist/grapes.min.js';
    script.onload = function() {
        // Rebrand to PageMade
        window.pagemade = window.grapesjs;
        window.pm = window.grapesjs;
        
        // Add PageMade branding
        if (window.pagemade && window.pagemade.plugins) {
            console.log('‚úÖ PageMade Editor (pm) loaded successfully');
            console.log('üîß Based on GrapeJS 0.20.3 - Customized for PageMade');
        }
        
        // Clean up original reference
        delete window.grapesjs;
    };
    script.onerror = function() {
        console.error('‚ùå Failed to load PageMade Editor core');
    };
    document.head.appendChild(script);
})();